    /// <summary>
    /// Returns the whole text.
    /// </summary>
    public static string CreateAllText()
    {
      return LongText;
    }

    /// <summary>
    /// Returns the whole text many times.
    /// </summary>
    public static string CreateAllTextManyTimes(int repeatCount)
    {
      if (repeatCount <= 0)
      {
        return string.Empty;
      }

      StringBuilder stringBuilder = new StringBuilder();

      for (int i = 0; i < repeatCount; i++)
      {
        stringBuilder.AppendLine(LongText);
      }

      return stringBuilder.ToString();
    }

    /// <summary>
    /// Create text.
    /// </summary>
    /// <param name="type">The type of text to be created.</param>
    /// <param name="amount">The amount of text to create.</param>
    /// <returns>A string with the created text.</returns>
    public static string CreateText(LipsumType type, int amount)
    {
      switch (type)
      {
        case LipsumType.Words:
          return GenerateWords(amount);

        case LipsumType.Paragraphs:
          return GenerateParagraphs(amount);

        case LipsumType.Bytes:
          return GenerateBytes(amount);

        case LipsumType.Lists:
          return GenerateList(amount);

        default:
          return GenerateWords(amount);
      }
    }

    /// <summary>
    /// Generate words.
    /// </summary>
    /// <param name="count">The number of words to be generated.</param>
    /// <returns>A string with <paramref name="count"/> words generated.</returns>
    private static string GenerateWords(int count)
    {
      StringBuilder stringBuilder = new StringBuilder();

      for (int i = 0; i <= count; i++)
      {
        stringBuilder.Append(GetWord(LongText, i));
      }

      return stringBuilder.ToString();
    }

    /// <summary>
    /// Get the nth word from a long text string.
    /// </summary>
    /// <param name="longText">The long text.</param>
    /// <param name="i">The nth index.</param>
    /// <returns>The <paramref name="i"/>th word.</returns>
    private static string GetWord(string longText, int i)
    {
      var textArray = longText.Split(' ');
      if (i > textArray.Length - 1)
      {
        return textArray[textArray.Length - 1];
      }
      else
      {
        return textArray[i];
      }
    }

    private static string GenerateParagraphs(int count)
    {
      return string.Join("\n\n", Enumerable.Repeat("Lorem ipsum dolor sit amet.", count));
    }

    private static string GenerateBytes(int count)
    {
      return new string('A', count);
    }

    private static string GenerateList(int count)
    {
      return string.Join("\n", Enumerable.Range(1, count).Select(i => $"- Item {i}"));
    }
